mapper.insert:
insert into user_goods (ID, user_id, goods_id
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{goodsId,jdbcType=INTEGER}
  )

mapper.update:
update user_goods
set user_id = #{userId,jdbcType=INTEGER},
  goods_id = #{goodsId,jdbcType=INTEGER}
where ID = #{id,jdbcType=INTEGER}

mapper.select:
select ID, user_id, goods_id
from user_goods
where ID = #{id,jdbcType=INTEGER}

select ID, user_id, goods_id
from user_goods
where goods_id = #{goodsId,jdbcType=INTEGER}

select ID, user_id, goods_id
from user_goods
where user_id = #{userId,jdbcType=INTEGER}

select ID, user_id, goods_id
from user_goods
where goods_id = #{goodsId,jdbcType=INTEGER} and user_id = #{userId,jdbcType=INTEGER}
